import { NextFunction, Response } from 'express';
import type { AuthRequest } from './auth';
import { logAdminAction } from '../services/adminActivityLogger';
import { storage } from '../storage';

export function activityLogger() {
  return async (req: AuthRequest, res: Response, next: NextFunction) => {
    const startedAt = Date.now();

    res.on('finish', async () => {
      try {
        // Only log successful mutating requests by admins
        if (!req.user || req.user.role !== 'admin') return;
        if (!['POST', 'PUT', 'PATCH', 'DELETE'].includes(req.method)) return;
        if (res.statusCode >= 400) return;

        const path = req.originalUrl || req.url;
        const method = req.method;

        let entityType: Parameters<typeof logAdminAction>[1]['entityType'] = 'other';
        let action = `${method} ${path}`;
        let description = action;
        let entityId: string | undefined = undefined;

        const adminName = [req.user?.firstName, req.user?.lastName].filter(Boolean).join(' ').trim() || req.user?.email || 'Admin';

        // Admin users management
        if (path.startsWith('/api/admin/users')) {
          entityType = 'user';
          if (method === 'POST') {
            const full = [req.body?.firstName, req.body?.lastName].filter(Boolean).join(' ');
            action = 'create_user';
            description = `${adminName} created a user${full ? `: ${full}` : ''}${req.body?.email ? ` (${req.body.email})` : ''}`;
          } else if (method === 'PATCH' && path.includes('/role')) {
            action = 'change_user_role';
            entityId = (req.params as any)?.id ? String((req.params as any).id) : undefined;
            description = `${adminName} changed user role${entityId ? ` for user ${entityId}` : ''}${req.body?.role ? ` to ${req.body.role}` : ''}`;
          } else if (method === 'DELETE') {
            action = 'delete_user';
            entityId = (req.params as any)?.id ? String((req.params as any).id) : undefined;
            description = `${adminName} deleted a user${entityId ? `: ${entityId}` : ''}`;
          }
        }

        // Problem sets (act as assignments)
        if (path.startsWith('/api/problem-sets')) {
          entityType = 'problemSet';
          entityId = (req.params as any)?.id ? String((req.params as any).id) : undefined;
          if (method === 'POST' && !entityId) {
            action = 'create_problem_set';
            description = `${adminName} created a problem set${req.body?.title ? `: ${req.body.title}` : ''}`;
          } else if (method === 'PUT') {
            action = 'update_problem_set';
            description = `${adminName} updated a problem set${req.body?.title ? `: ${req.body.title}` : ''}`;
          } else if (method === 'DELETE') {
            action = 'delete_problem_set';
            description = `${adminName} deleted a problem set${entityId ? ` #${entityId}` : ''}`;
          }
        }

        // Course enroll via admin
        if (path.match(/^\/api\/courses\/\d+\/enroll/)) {
          entityType = 'enrollment';
          const courseId = Number((req.params as any)?.id);
          let courseName = '';
          try {
            const course = await storage.getCourse(courseId);
            courseName = (course as any)?.title || '';
          } catch {}
          const targetUserId = req.body?.userId && req.body.userId !== 'self' ? req.body.userId : req.user?.id;
          let targetUserName = '';
          if (targetUserId) {
            try {
              const u = await storage.getUser(String(targetUserId));
              targetUserName = [u?.firstName, u?.lastName].filter(Boolean).join(' ').trim() || u?.email || String(targetUserId);
            } catch {}
          }
          action = 'enroll_user_in_course';
          description = `${adminName} enrolled ${targetUserName || 'a user'} in course${courseName ? `: ${courseName}` : courseId ? ` #${courseId}` : ''}`;
          entityId = String(courseId);
        }

        await logAdminAction(req, {
          action,
          description,
          entityType,
          entityId,
          metadata: {
            statusCode: res.statusCode,
            durationMs: Date.now() - startedAt,
            query: req.query,
            bodyKeys: req.body ? Object.keys(req.body) : [],
          },
        });
      } catch (e) {
        // do not block response
      }
    });

    next();
  };
}

client/src/components/CodeEditor.tsx.content:
import { useEffect, useRef } from 'react';
import * as monaco from 'monaco-editor';

export interface CodeEditorProps {
  value: string;
  onChange: (value: string) => void;
  language: string;
}

export function CodeEditor({ value, onChange, language }: CodeEditorProps) {
  const editorRef = useRef<monaco.editor.IStandaloneCodeEditor | null>(null);
  const containerRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    if (containerRef.current) {
      editorRef.current = monaco.editor.create(containerRef.current, {
        value,
        language,
        theme: 'vs-dark',
        minimap: { enabled: false },
        fontSize: 14,
        lineNumbers: 'on',
        scrollBeyondLastLine: false,
        automaticLayout: true,
        tabSize: 2,
        wordWrap: 'on',
      });

      editorRef.current.onDidChangeModelContent(() => {
        if (editorRef.current) {
          onChange(editorRef.current.getValue());
        }
      });

      return () => {
        editorRef.current?.dispose();
      };
    }
  }, []);

  useEffect(() => {
    if (editorRef.current && value !== editorRef.current.getValue()) {
      editorRef.current.setValue(value);
    }
  }, [value]);

  useEffect(() => {
    if (editorRef.current) {
      monaco.editor.setModelLanguage(editorRef.current.getModel()!, language);
    }
  }, [language]);

  return (
    <div 
      ref={containerRef} 
      className="w-full h-full min-h-[300px]"
      data-testid="code-editor"
    />
  );
}

client/src/pages/AdminVersionHistory.tsx.content:
import { useMemo, useState } from 'react';
import { useQuery } from '@tanstack/react-query';
import { useLocation } from 'wouter';
import { Card, CardHeader, CardTitle, CardDescription, CardContent } from '@/components/ui/card';
import { Input } from '@/components/ui/input';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Button } from '@/components/ui/button';
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from '@/components/ui/table';
import { Pagination, PaginationContent, PaginationItem, PaginationLink } from '@/components/ui/pagination';
import { config } from '@/config';

interface HistoryItem {
  id: string;
  action: string;
  description?: string;
  entityType: string;
  entityId?: string;
  adminName?: string;
  adminId?: string;
  createdAt: string;
}

export default function AdminVersionHistory() {
  const [, setLocation] = useLocation();
  const token = useMemo(() => localStorage.getItem('token') || '', []);

  const [page, setPage] = useState(1);
  const [pageSize, setPageSize] = useState(20);
  const [entityType, setEntityType] = useState<string>('');
  const [adminId, setAdminId] = useState<string>('');
  const [search, setSearch] = useState<string>('');

  const queryString = useMemo(() => {
    const params = new URLSearchParams();
    params.set('page', String(page));
    params.set('pageSize', String(pageSize));
    if (entityType) params.set('entityType', entityType);
    if (adminId) params.set('adminId', adminId);
    if (search) params.set('search', search);
    return params.toString();
  }, [page, pageSize, entityType, adminId, search]);

  const { data, isLoading } = useQuery({
    queryKey: ['admin', 'version-history', queryString],
    queryFn: async () => {
      const res = await fetch(`${config.apiUrl}/api/admin/version-history?${queryString}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      if (!res.ok) throw new Error(await res.text());
      return res.json() as Promise<{ items: HistoryItem[]; total: number; page: number; pageSize: number; totalPages: number }>
    },
  });

  const items = data?.items || [];

  return (
    <div className="container mx-auto p-4 sm:p-6 space-y-4">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-2xl font-bold">Version History</h1>
          <p className="text-muted-foreground">All admin actions across the platform</p>
        </div>
      </div>

      <Card>
        <CardHeader>
          <CardTitle>Filters</CardTitle>
          <CardDescription>Refine activity list</CardDescription>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-1 md:grid-cols-4 gap-3">
            <Input placeholder="Search action/description" value={search} onChange={(e) => setSearch(e.target.value)} />
            <Select value={entityType || 'all'} onValueChange={(v) => setEntityType(v === 'all' ? '' : v)}>
              <SelectTrigger>
                <SelectValue placeholder="Entity Type" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">All Entities</SelectItem>
                <SelectItem value="problem">Problem</SelectItem>
                <SelectItem value="problemSet">Problem Set</SelectItem>
                <SelectItem value="course">Course</SelectItem>
                <SelectItem value="courseModule">Course Module</SelectItem>
                <SelectItem value="user">User</SelectItem>
                <SelectItem value="contest">Contest</SelectItem>
                <SelectItem value="enrollment">Enrollment</SelectItem>
                <SelectItem value="announcement">Announcement</SelectItem>
                <SelectItem value="other">Other</SelectItem>
              </SelectContent>
            </Select>
            <Input placeholder="Admin ID (optional)" value={adminId} onChange={(e) => setAdminId(e.target.value)} />
            <Select value={String(pageSize)} onValueChange={(v) => setPageSize(parseInt(v, 10))}>
              <SelectTrigger>
                <SelectValue placeholder="Page size" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="10">10</SelectItem>
                <SelectItem value="20">20</SelectItem>
                <SelectItem value="50">50</SelectItem>
                <SelectItem value="100">100</SelectItem>
              </SelectContent>
            </Select>
          </div>
        </CardContent>
      </Card>

      <Card>
        <CardHeader>
          <CardTitle>Activity</CardTitle>
          <CardDescription>Showing {items.length} of {data?.total || 0} entries</CardDescription>
        </CardHeader>
        <CardContent>
          {isLoading ? (
            <div>Loading...</div>
          ) : (
            <div className="space-y-4">
              <Table>
                <TableHeader>
                  <TableRow>
                    <TableHead>When</TableHead>
                    <TableHead>Admin</TableHead>
                    <TableHead>Action</TableHead>
                    <TableHead>Entity</TableHead>
                    <TableHead>Description</TableHead>
                  </TableRow>
                </TableHeader>
                <TableBody>
                  {items.map((i) => (
                    <TableRow key={i.id}>
                      <TableCell>{new Date(i.createdAt).toLocaleString()}</TableCell>
                      <TableCell>{i.adminName || i.adminId}</TableCell>
                      <TableCell className="whitespace-pre-wrap">{i.action}</TableCell>
                      <TableCell>{i.entityType}{i.entityId ? ` (#${i.entityId})` : ''}</TableCell>
                      <TableCell className="max-w-[480px] truncate" title={i.description || ''}>{i.description}</TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>

              {data && data.totalPages > 1 && (
                <Pagination>
                  <PaginationContent>
                    {Array.from({ length: data.totalPages }).map((_, idx) => (
                      <PaginationItem key={idx}>
                        <PaginationLink isActive={data.page === idx + 1} onClick={() => setPage(idx + 1)}>
                          {idx + 1}
                        </PaginationLink>
                      </PaginationItem>
                    ))}
                  </PaginationContent>
                </Pagination>
              )}
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

server/models/ContestParticipant.ts.content:
import mongoose, { Document } from 'mongoose';

export interface IContestParticipant {
  id: string;
  contestId: string;
  userId: string; // store as string to match req.user.id
  registrationTime: Date;
  totalScore: number;
  totalPenalty: number;
  submissions: string[]; // submission ids
  problemsAttempted: string[];
  problemsSolved: string[];
  isDisqualified: boolean;
  rank?: number;
  contestEndMethod?: 'manually_ended' | 'time_expired' | null;
  enrollmentType: 'admin' | 'qr'; // New field to track enrollment method
}

interface IContestParticipantDocument extends Omit<IContestParticipant, 'id'>, Document {
  id: string;
}

const contestParticipantSchema = new mongoose.Schema<IContestParticipantDocument>({
  id: { type: String, required: true, unique: true, index: true },
  contestId: { type: String, required: true, index: true },
  userId: { type: String, required: true, index: true },
  registrationTime: { type: Date, default: Date.now },
  totalScore: { type: Number, default: 0 },
  totalPenalty: { type: Number, default: 0 },
  submissions: { type: [String], default: [] },
  problemsAttempted: { type: [String], default: [] },
  problemsSolved: { type: [String], default: [] },
  isDisqualified: { type: Boolean, default: false },
  rank: { type: Number },
  contestEndMethod: { type: String, enum: ['manually_ended', 'time_expired', null], default: null },
  enrollmentType: { 
    type: String, 
    enum: ['admin', 'qr'], 
    required: [true, 'Enrollment type is required'],
    default: 'qr' // Default to QR for backward compatibility
  },
}, { timestamps: true });

contestParticipantSchema.index({ contestId: 1, userId: 1 }, { unique: true });

export const ContestParticipant = mongoose.model<IContestParticipantDocument>('ContestParticipant', contestParticipantSchema);

