function AppContent() {
  const { user, isLoading, isAuthenticated } = useAuth();
  const [location, setLocation] = useLocation();

  // Public route wrapper
  const PublicRoute = ({ children }: { children: React.ReactNode }) => {
    if (isAuthenticated) {
      return <Redirect to="/dashboard" />;
    }
    return <>{children}</>;
  };

  // Admin route wrapper
  const AdminRoute = ({ children }: { children: React.ReactNode }) => {
    if (!isAuthenticated || user?.role !== 'admin') {
      return <Redirect to="/dashboard" />;
    }
    return <>{children}</>;
  };

  if (isLoading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-green-500"></div>
      </div>
    );
  }

  return (
    <Switch>
      {/* Public routes - always accessible */}
      <Route path="/auth-callback">
        <AuthCallback />
      </Route>
      <Route path="/login">
        <PublicRoute><LoginPage /></PublicRoute>
      </Route>
      <Route path="/register">
        <PublicRoute><RegisterPage /></PublicRoute>
      </Route>
      <Route path="/forgot-password">
        <ForgotPassword />
      </Route>
      <Route path="/reset-password">
        <PublicRoute><ResetPassword /></PublicRoute>
      </Route>
      <Route path="/enroll/:courseId">
        <EnrollmentPage />
      </Route>
      <Route path="/enroll-problem-set/:problemSetId">
        <ProblemSetEnrollmentPage />
      </Route>
      <Route path="/contest-enrollment/:contestId">
        <ContestEnrollmentPage />
      </Route>
      <Route path="/">
        <PublicRoute><Landing /></PublicRoute>
      </Route>

      {/* Full-screen routes without sidebar */}
      {isAuthenticated && (
        <>
          <Route path="/problems/:id">
            <ProblemDetail />
          </Route>
          <Route path="/courses/:courseId/modules/:moduleId">
            <CourseModuleViewer />
          </Route>
          <Route path="/contests/:contestId/problems/:problemId">
            <ContestProblemsPage />
          </Route>
          <Route path="/contests/:contestId/problems">
            <ContestProblemsPage />
          </Route>
        </>
      )}

      {/* Protected routes wrapped in Layout */}
      {isAuthenticated ? (
        <Layout>
          <Switch>
            <Route path="/" component={() => <Redirect to="/dashboard" />} />
            <Route path="/dashboard">
              <Dashboard />
            </Route>
            <Route path="/assignments">
              <ProblemSets />
            </Route>
            <Route path="/problem-sets/:id">
              <ProblemSetDetail />
            </Route>
            <Route path="/contests">
              {user?.role === 'admin' ? <AdminContests /> : <Contests />}
            </Route>
            <Route path="/contests/:contestId">
              <ContestDetailsPage />
            </Route>
            <Route path="/contests/:contestId/details">
              <ContestDetailsPage />
            </Route>
            <Route path="/contests/:contestId/leaderboard">
              <ContestLeaderboardPage />
            </Route>
            <Route path="/contests/:contestId/results">
              <ContestResultsPage />
            </Route>
            <Route path="/courses">
              {user?.role === 'admin' ? <AdminCourses /> : <Courses />}
            </Route>
            <Route path="/courses/:courseId">
              {user?.role === 'admin' ? (
                <AdminRoute>
                  <CourseDetail />
                </AdminRoute>
              ) : (
                <StudentCourseView />
              )}
            </Route>

            <Route path="/assignments/:id">
              <AssignmentSubmission />
            </Route>
            <Route path="/admin/assignments">
              <AdminRoute>
                <AdminAssignments />
              </AdminRoute>
            </Route>
            <Route path="/admin/assignments/create">
