              </AdminRoute>
            </Route>
            <Route path="/admin/assignments/:assignmentId/analytics">
              <AdminRoute>
                <AssignmentAnalytics />
              </AdminRoute>
            </Route>
            <Route path="/admin/assignments/:assignmentId/submissions">
