              <AdminRoute>
                <AssignmentAnalytics />
              </AdminRoute>
            </Route>
            <Route path="/admin/assignments/:assignmentId/users/:userId/analytics">
              <AdminRoute>
                <UserAssignmentAnalytics />
              </AdminRoute>
            </Route>
            {/* <Route path="/admin/courses/:courseId/analytics">
              <AdminRoute>
                <CourseAnalytics />
              </AdminRoute>
            </Route> */}


            <Route path="/admin/problem-sets">
              <AdminRoute>
                <ProblemSetManagement />
