client/src/services/problemService.ts.content:
import { apiClient } from '@/lib/apiClient';

export interface Example {
  input: string;
  output: string;
  explanation?: string;
}

export interface TestCase {
  input: string;
  expectedOutput: string;
  isHidden: boolean;
}

export interface StarterCode {
  python?: string;
  javascript?: string;
  cpp?: string;
