  async executeWithCustomInput(
    code: string,
    language: string,
    customInput: string
  ): Promise<{
    output: string;
    error: string | null;
    runtime: number;
    memory: number;
    input: string;
  }> {
    console.log(`[EXEC-SERVICE] üéØ Executing code with custom input for ${language}`);

