import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { CheckCircle, Clock, Lock } from "lucide-react";
import { Link } from "wouter";

interface Problem {
  id: number;
  title: string;
  difficulty: string;
  tags?: string[];
}

interface Submission {
  id: number;
  problemId: number;
  status: string;
  submittedAt: string;
}

export function RecentProblems() {
  const { data: problems, isLoading: problemsLoading } = useQuery<Problem[]>({
    queryKey: ["/api/problems"],
    retry: false,
  });

  const { data: userSubmissions, isLoading: submissionsLoading } = useQuery<Submission[]>({
    queryKey: ["/api/submissions"],
    retry: false,
  });

  const isLoading = problemsLoading || submissionsLoading;

  if (isLoading) {
    return (
      <Card>
        <CardHeader>
          <CardTitle>Recent Problems</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="space-y-4">
            {[...Array(3)].map((_, i) => (
              <div key={i} className="animate-pulse">
                <div className="flex items-center justify-between p-4 bg-gray-50 dark:bg-gray-800 rounded-lg">
                  <div className="flex items-center space-x-4">
                    <div className="w-10 h-10 bg-gray-200 dark:bg-gray-700 rounded-lg"></div>
                    <div>
                      <div className="h-4 bg-gray-200 dark:bg-gray-700 rounded w-32 mb-2"></div>
                      <div className="h-3 bg-gray-200 dark:bg-gray-700 rounded w-20"></div>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </CardContent>
      </Card>
    );
  }

  // Get first 3 problems for recent display
  const recentProblems = problems?.slice(0, 3) || [];

  const getProblemStatus = (problemId: number) => {
    if (!userSubmissions) return { icon: Lock, text: "Locked", color: "text-gray-500" };

    const problemSubmissions = userSubmissions.filter(s => s.problemId === problemId);
    if (problemSubmissions.length === 0) {
      return { icon: Lock, text: "Not Started", color: "text-gray-500" };
    }

    const hasAccepted = problemSubmissions.some(s => s.status === "accepted");
    if (hasAccepted) {
      return { icon: CheckCircle, text: "Solved", color: "text-green-600 dark:text-green-400" };
    }

    return { icon: Clock, text: "In Progress", color: "text-yellow-600 dark:text-yellow-400" };
  };

  const getDifficultyColor = (difficulty: string) => {
    switch (difficulty.toLowerCase()) {
      case "easy":
        return "bg-green-100 text-green-800 dark:bg-green-900/20 dark:text-green-400";
      case "medium":
        return "bg-yellow-100 text-yellow-800 dark:bg-yellow-900/20 dark:text-yellow-400";
      case "hard":
        return "bg-red-100 text-red-800 dark:bg-red-900/20 dark:text-red-400";
      default:
        return "bg-gray-100 text-gray-800 dark:bg-gray-900/20 dark:text-gray-400";
    }
  };

  return (
    <Card>
      <CardHeader className="border-b border-gray-200 dark:border-gray-800">
        <CardTitle>Recent Problems</CardTitle>
      </CardHeader>
      <CardContent className="p-6">
        <div className="space-y-4">
          {recentProblems.map((problem: Problem) => {
            const status = getProblemStatus(problem.id);
            const StatusIcon = status.icon;

            return (
              <div
                key={problem.id}
                className="flex items-center justify-between p-4 bg-gray-50 dark:bg-gray-800 rounded-lg hover:bg-gray-100 dark:hover:bg-gray-700 transition-colors cursor-pointer"
              >
                <div className="flex items-center space-x-4">
                  <div className="w-10 h-10 bg-gray-200 dark:bg-gray-700 rounded-lg flex items-center justify-center">
                    <StatusIcon className={`h-4 w-4 ${status.text === "Solved" ? "text-green-500" : status.text === "In Progress" ? "text-yellow-500" : "text-gray-500"}`} />
                  </div>
                  <div>
                    <h3 className="font-medium text-gray-900 dark:text-white">
                      {problem.title}
                    </h3>
                    <div className="flex items-center space-x-2 mt-1">
                      <Badge className={getDifficultyColor(problem.difficulty)}>
                        {problem.difficulty}
                      </Badge>
                      {problem.tags && problem.tags.length > 0 && (
                        <span className="text-sm text-gray-600 dark:text-gray-400">
                          {problem.tags[0]}
                        </span>
                      )}
                    </div>
                  </div>
                </div>
                <div className="text-right">
                  <p className={`text-sm font-medium ${status.color}`}>
                    {status.text}
                  </p>
                  {status.text === "Solved" && userSubmissions && (
                    <p className="text-xs text-gray-500">
                      {new Date(userSubmissions.find(s => s.problemId === problem.id && s.status === "accepted")?.submittedAt || '').toLocaleString()}
                    </p>
                  )}
                  {status.text === "In Progress" && userSubmissions && (
                    <p className="text-xs text-gray-500">
                      Last attempt {new Date(userSubmissions.find(s => s.problemId === problem.id)?.submittedAt || '').toLocaleString()}
                    </p>
                  )}
                </div>
              </div>
            );
          })}
        </div>

        <div className="mt-6 text-center">
          <Button asChild className="bg-green-500 hover:bg-green-600 text-white">
            <Link href="/problems">View All Problems</Link>
          </Button>
        </div>
      </CardContent>
    </Card>
  );
}

client/src/pages/admin/CreateModule.tsx.content:
import { useState } from 'react';
import { useParams, useLocation } from 'wouter';
import { useMutation, useQueryClient } from '@tanstack/react-query';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Textarea } from '@/components/ui/textarea';
import { Label } from '@/components/ui/label';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { ChevronLeft, Save, Loader2 } from 'lucide-react';
import axios from 'axios';
import { config } from '@/config';

interface CourseModule {
  id: number;
  courseId: number;
  title: string;
  description?: string;
  order: number;
  textContent?: string;
  videoUrl?: string;
  codeExample?: string;
  language?: string;
  expectedOutput?: string;
}

const api = axios.create({
  baseURL: `${config.apiUrl}/api`,
});

// Add request interceptor to include Authorization header
api.interceptors.request.use((config) => {
  const token = localStorage.getItem('token');
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
  }
  return config;
});

export default function CreateModule() {
  const { courseId: courseIdParam } = useParams<{ courseId: string }>();
  const [, setLocation] = useLocation();
  const queryClient = useQueryClient();
  const courseId = courseIdParam ? parseInt(courseIdParam) : NaN;

  const [formData, setFormData] = useState<Partial<CourseModule>>({
    title: '',
    description: '',
    order: 1,
    textContent: '',
    videoUrl: '',
    codeExample: '',
    language: 'javascript',
    expectedOutput: '',
  });

  const createModule = useMutation({
    mutationFn: async (data: Partial<CourseModule>) => {
      if (!courseId || isNaN(courseId)) {
        throw new Error('Course ID is required');
      }
      const response = await api.post(`/courses/${courseId}/modules`, data);
      return response.data;
    },
    onSuccess: () => {
      // Invalidate and refetch all related queries
      queryClient.invalidateQueries({ queryKey: ['course', courseId] });
      queryClient.invalidateQueries({ queryKey: ['course-modules', courseId] });
      queryClient.invalidateQueries({ queryKey: ['courses'] });
      queryClient.invalidateQueries({ queryKey: ['/api/users/me/courses'] });

      // Force a refetch of the course modules
      queryClient.refetchQueries({ queryKey: ['course-modules', courseId] });

      alert('Module created successfully');
      setLocation(`/admin/courses/${courseId}`);
    },
    onError: (error: any) => {
      alert(error.response?.data?.message || 'Failed to create module');
    },
  });

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!courseId || isNaN(courseId)) {
      alert('Course ID is missing');
      return;
    }

    // Validate form data
    if (!formData.title?.trim()) {
      alert('Module title is required');
      return;
    }

    if (typeof formData.order !== 'number' || formData.order < 1) {
      alert('Order must be a positive number');
      return;
    }

    createModule.mutate(formData);
  };

  if (!courseId || isNaN(courseId)) {
    return (
      <div className="container mx-auto py-8 px-6">
        <div className="text-center">
          <h1 className="text-2xl font-bold mb-4">Invalid Course ID</h1>
          <p className="text-muted-foreground mb-4">
            The course ID is missing or invalid.
          </p>
          <Button onClick={() => setLocation('/admin/courses')}>
            <ChevronLeft className="h-4 w-4 mr-2" />
            Back to Courses
          </Button>
        </div>
      </div>
    );
  }

  return (
    <div className="container mx-auto py-8 px-6">
      <div className="mb-8">
        <Button 
          variant="ghost" 
          onClick={() => setLocation(`/admin/courses/${courseId}`)}
          className="mb-4"
        >
          <ChevronLeft className="h-4 w-4 mr-2" />
          Back to Course
        </Button>

        <h1 className="text-3xl font-bold mb-2">Create New Module</h1>
        <p className="text-muted-foreground">
          Add a new learning module to this course
        </p>
      </div>

      <Card>
        <CardHeader>
          <CardTitle>Module Information</CardTitle>
        </CardHeader>
        <CardContent>
          <form onSubmit={handleSubmit} className="space-y-6">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <div className="space-y-2">
                <Label htmlFor="title">Module Title</Label>
                <Input
                  id="title"
                  value={formData.title}
                  onChange={(e) => setFormData({ ...formData, title: e.target.value })}
                  placeholder="Enter module title"
                  required
                />
              </div>

              <div className="space-y-2">
                <Label htmlFor="order">Order</Label>
                <Input
                  id="order"
                  type="number"
                  min="1"
                  value={formData.order}
                  onChange={(e) => setFormData({ ...formData, order: parseInt(e.target.value) || 1 })}
                  placeholder="Module order"
                  required
                />
              </div>
            </div>

            <div className="space-y-2">
              <Label htmlFor="description">Description</Label>
              <Textarea
                id="description"
                value={formData.description}
                onChange={(e) => setFormData({ ...formData, description: e.target.value })}
                placeholder="Enter module description"
                rows={3}
              />
            </div>

            <div className="space-y-2">
              <Label htmlFor="textContent">Content</Label>
              <Textarea
                id="textContent"
                value={formData.textContent}
                onChange={(e) => setFormData({ ...formData, textContent: e.target.value })}
                placeholder="Enter the main content/lesson text"
                rows={6}
              />
            </div>

            <div className="space-y-2">
              <Label htmlFor="videoUrl">Video URL (Optional)</Label>
              <Input
                id="videoUrl"
                type="url"
                value={formData.videoUrl}
                onChange={(e) => setFormData({ ...formData, videoUrl: e.target.value })}
                placeholder="https://youtube.com/watch?v=..."
              />
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <div className="space-y-2">
                <Label htmlFor="language">Programming Language (Optional)</Label>
                <select
                  id="language"
                  value={formData.language}
                  onChange={(e) => setFormData({ ...formData, language: e.target.value })}
                  className="w-full p-2 border border-input bg-background rounded-md"
                >
                  <option value="javascript">JavaScript</option>
                  <option value="python">Python</option>
                  <option value="java">Java</option>
                  <option value="cpp">C++</option>
                  <option value="c">C</option>
                </select>
              </div>

              <div className="space-y-2">
                <Label htmlFor="expectedOutput">Expected Output (Optional)</Label>
                <Textarea
                  id="expectedOutput"
                  value={formData.expectedOutput}
                  onChange={(e) => setFormData({ ...formData, expectedOutput: e.target.value })}
                  placeholder="Expected output for code examples"
                  rows={2}
                />
              </div>
            </div>

            <div className="space-y-2">
              <Label htmlFor="codeExample">Code Example (Optional)</Label>
              <Textarea
                id="codeExample"
                value={formData.codeExample}
                onChange={(e) => setFormData({ ...formData, codeExample: e.target.value })}
                placeholder="Enter code example or starter code"
                rows={8}
                className="font-mono text-sm"
              />
            </div>

            <div className="flex justify-end space-x-4">
              <Button
                type="button"
                variant="outline"
                onClick={() => setLocation(`/admin/courses/${courseId}`)}
              >
                Cancel
              </Button>
              <Button 
                type="submit"
                disabled={createModule.isPending}
              >
                {createModule.isPending ? (
                  <>
                    <Loader2 className="h-4 w-4 mr-2 animate-spin" />
                    Creating...
                  </>
                ) : (
                  <>
                    <Save className="h-4 w-4 mr-2" />
                    Create Module
                  </>
                )}
              </Button>
            </div>
          </form>
        </CardContent>
      </Card>
    </div>
  );
}

execution-system/simple-docker-api.js.content:
const express = require('express');
const Docker = require('dockerode');
const cors = require('cors');
const { v4: uuidv4 } = require('uuid');

const app = express();
const docker = new Docker({ socketPath: '/var/run/docker.sock' });

// Middleware
app.use(cors());
app.use(express.json({ limit: '10mb' }));

// Supported languages and their Docker images
const LANGUAGE_IMAGES = {
  python: 'codearena/executor-python:latest',
  javascript: 'codearena/executor-javascript:latest',
  c: 'codearena/executor-c:latest',
  cpp: 'codearena/executor-cpp:latest',
  java: 'codearena/executor-java:latest'
};

// Health check endpoint
app.get('/health', (req, res) => {
  res.json({
    status: 'healthy',
    timestamp: new Date().toISOString(),
    docker: true,
    languages: Object.keys(LANGUAGE_IMAGES)
  });
});

// Execute code endpoint
app.post('/api/execute', async (req, res) => {
  try {
    const { code, language, input = '' } = req.body;

    if (!code || !language) {
      return res.status(400).json({
        error: 'Code and language are required'
      });
    }

    const imageName = LANGUAGE_IMAGES[language.toLowerCase()];
    if (!imageName) {
      return res.status(400).json({
        error: `Unsupported language: ${language}. Supported languages: ${Object.keys(LANGUAGE_IMAGES).join(', ')}`
      });
    }

    console.log(`[DOCKER-API] Executing ${language} code`);

    // Create container
    const container = await docker.createContainer({
      Image: imageName,
      Cmd: ['/bin/sh', '-c', `echo '${code.replace(/'/g, "'\"'\"'")}' > /tmp/code && echo '${input.replace(/'/g, "'\"'\"'")}' > /tmp/input && cd /tmp && /app/execute.sh`],
      HostConfig: {
        Memory: 128 * 1024 * 1024, // 128MB
        MemorySwap: 128 * 1024 * 1024,
        CpuPeriod: 100000,
        CpuQuota: 50000, // 0.5 CPU
        PidsLimit: 64,
        Ulimits: [
          { Name: 'nofile', Soft: 1024, Hard: 1024 },
          { Name: 'nproc', Soft: 64, Hard: 64 }
        ],
        AutoRemove: true
      },
      Env: [
        'CODE=' + code,
        'INPUT=' + input,
        'TIME_LIMIT=10',
        'MEMORY_LIMIT=128m',
        'OUTPUT_LIMIT=64k'
      ]
    });

    const startTime = Date.now();

    // Start container
    await container.start();

    // Wait for container to finish
    const result = await container.wait();

    const endTime = Date.now();
    const runtime = endTime - startTime;

    // Get container logs
    const logs = await container.logs({
      stdout: true,
      stderr: true,
      tail: 1000
    });

    const output = logs.toString('utf8').trim();

    // Check if execution was successful
    if (result.StatusCode === 0) {
      res.json({
        output: output,
        runtime: runtime,
        memory: 0, // We'll get this from the container later
        error: null
      });
    } else {
      res.json({
        output: '',
        runtime: runtime,
        memory: 0,
        error: output || 'Execution failed'
      });
    }

  } catch (error) {
    console.error('[DOCKER-API] Error:', error);
    res.status(500).json({
      error: 'Internal server error: ' + error.message
    });
  }
});

// Get supported languages
app.get('/api/languages', (req, res) => {
  res.json({
    languages: Object.keys(LANGUAGE_IMAGES),
    images: LANGUAGE_IMAGES
  });
});

// Start server
const PORT = process.env.PORT || 3001;
app.listen(PORT, () => {
  console.log(`üöÄ Simple Docker API running on port ${PORT}`);
  console.log(`üì¶ Supported languages: ${Object.keys(LANGUAGE_IMAGES).join(', ')}`);
});

