    disableRightClick: z.boolean().default(true),
    disableExtensions: z.boolean().default(true),
    enablePlagiarismDetection: z.boolean().default(true),
    allowedLanguages: z.array(z.string()).default(["python", "javascript", "cpp", "java", "c"]),
