                          </SelectContent>
                        </Select>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <FormField
                    control={form.control}
                    name="title"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel className="flex items-center space-x-2">
                          <span>Title</span>
                          {selectedProblemId && (
                            <Badge variant="secondary" className="bg-blue-100 text-blue-800">
                              Editable
