        variant: "destructive",
      });
    },
  });

  const createAnnouncementMutation = useMutation({
    mutationFn: async (data: z.infer<typeof announcementSchema>) => {
      const res = await fetch(`${config.apiUrl}/api/admin/announcements`, {
        method: 'POST',
        headers: {
