    },
  });

  const announcementForm = useForm<z.infer<typeof announcementSchema>>({
    resolver: zodResolver(announcementSchema),
    defaultValues: {
      title: "",
      content: "",
      priority: "medium",
      targetAudience: ["all"],
      isVisible: true,
    },
  });

  const createUserForm = useForm<z.infer<typeof createUserSchema>>({
    resolver: zodResolver(createUserSchema),
    defaultValues: {
      firstName: "",
      lastName: "",
      email: "",
      password: "",
      role: "student",
    },
  });

  // Memoized handlers to prevent re-renders
  const [pendingRoleChange, setPendingRoleChange] = useState<{
    userId: string;
    newRole: string;
    currentRole?: string;
    name?: string;
  } | null>(null);

  const handleUpdateUserRole = useCallback((userId: string, role: string, currentRole?: string, name?: string) => {
    if (currentRole !== role) {
      setPendingRoleChange({ userId, newRole: role, currentRole, name });
      return;
