  description?: string;
  courseTag: string;
  deadline?: string;
  questions: any[];
  maxAttempts: number;
  isVisible: boolean;
  autoGrade: boolean;
  isEnrolled?: boolean;
  isCompleted?: boolean;
  createdBy?: string;
