import { Request, Response } from 'express';
import type { AuthRequest } from '../middleware/auth';
import { z } from 'zod';
import { insertCourseModuleSchema } from '../shared-schema';
import { CourseModule } from '../models/CourseModule';
import { executionServicePromise } from '../services/executionService';
import { storage } from '../storage';

export async function getModuleById(req: Request, res: Response) {
  try {
    const id = parseInt(req.params.id);
    const module = await storage.getCourseModule(id);
    if (!module) return res.status(404).json({ message: 'Module not found' });
    res.json(module);
  } catch (error) {
    console.error('Error fetching module:', error);
    res.status(500).json({ message: 'Failed to fetch module' });
  }
}

export async function createModule(req: AuthRequest, res: Response) {
  try {
    const courseId = parseInt(req.params.id);
    const raw = req.body || {};
    const sanitized = {
      ...raw,
      courseId,
      order: raw.order !== undefined ? Number(raw.order) : undefined,
      videoUrl: raw.videoUrl === '' ? undefined : raw.videoUrl,
      textContent: raw.textContent === '' ? undefined : raw.textContent,
      codeExample: raw.codeExample === '' ? undefined : raw.codeExample,
      expectedOutput: raw.expectedOutput === '' ? undefined : raw.expectedOutput,
      language: raw.language === '' ? undefined : raw.language,
      title: raw.title === '' ? undefined : raw.title,
      description: raw.description === '' ? undefined : raw.description,
    } as any;
    const validatedData = insertCourseModuleSchema.parse(sanitized);

    const module = await storage.createCourseModule(validatedData);
    res.status(201).json(module);
  } catch (error) {
    if (error instanceof z.ZodError) {
      return res.status(400).json({ message: 'Invalid data', errors: error.errors });
    }
    console.error('Error creating module:', error);
    res.status(500).json({ message: 'Failed to create module' });
  }
}

export async function updateModule(req: AuthRequest, res: Response) {
  try {
    const id = parseInt(req.params.id);
    const raw = req.body || {};
    const sanitized = {
      ...raw,
      order: raw.order !== undefined ? Number(raw.order) : undefined,
      videoUrl: raw.videoUrl === '' ? undefined : raw.videoUrl,
      textContent: raw.textContent === '' ? undefined : raw.textContent,
      codeExample: raw.codeExample === '' ? undefined : raw.codeExample,
      expectedOutput: raw.expectedOutput === '' ? undefined : raw.expectedOutput,
      language: raw.language === '' ? undefined : raw.language,
      title: raw.title === '' ? undefined : raw.title,
      description: raw.description === '' ? undefined : raw.description,
    } as any;
    const validatedData = insertCourseModuleSchema.partial().parse(sanitized);

    const module = await storage.updateCourseModule(id, validatedData);
    if (!module) return res.status(404).json({ message: 'Module not found' });
    res.json(module);
  } catch (error) {
    if (error instanceof z.ZodError) {
      return res.status(400).json({ message: 'Invalid data', errors: error.errors });
    }
    console.error('Error updating module:', error);
    res.status(500).json({ message: 'Failed to update module' });
  }
}

export async function deleteModule(req: AuthRequest, res: Response) {
  try {
    const id = parseInt(req.params.id);

    const module = await storage.getCourseModule(id);
    if (!module) return res.status(404).json({ message: 'Module not found' });

    await storage.deleteCourseModule(id);
    res.status(204).send();
  } catch (error) {
    console.error('Error deleting module:', error);
    res.status(500).json({ message: 'Failed to delete module' });
  }
}

export async function executeModule(req: Request, res: Response) {
  try {
    const { code, language, input } = req.body;

    if (!code || !language) {
      return res.status(400).json({ 
        message: 'Code and language are required' 
      });
    }

    console.log(`[MODULES-CONTROLLER] üöÄ Executing module code:`, {
      language,
      codeLength: code.length,
      hasInput: !!input
    });

    const executionService = await executionServicePromise;
    const result = await executionService.executeCode(code, language, input);

    console.log(`[MODULES-CONTROLLER] ‚úÖ Module execution completed:`, {
      success: !result.error,
      outputLength: result.output?.length || 0,
      error: result.error
    });

    res.json({
      success: !result.error,
      output: result.output,
      error: result.error,
      runtime: result.runtime,
      memory: result.memory
    });
  } catch (error: any) {
    console.error(`[MODULES-CONTROLLER] ‚ùå Module execution failed:`, error);
    res.status(500).json({ 
      message: 'Failed to execute code',
      error: error.message 
    });
  }
}

server/models/Contest.ts.content:
import mongoose, { Document } from 'mongoose';

export interface IContestProblem {
  id: string;
  originalProblemId?: number | string;
  title?: string;
  description?: string;
  difficulty?: string;
  points?: number;
  order?: number;
  timeLimit?: number;
  memoryLimit?: number;
  constraints?: string;
  inputFormat?: string;
  outputFormat?: string;
  maxSubmissions?: number;
  partialScoring?: boolean;
  customTestCases?: any[];
  customExamples?: any[];
  customStarterCode?: any;
  tags?: string[];
  notes?: string;
  examples?: any[];
  testCases?: any[];
  starterCode?: any;
}

export interface IContestAnnouncement {
  id: string;
  message: string;
  timestamp: Date;
  priority: 'low' | 'medium' | 'high';
  isGlobal?: boolean;
}

export interface IContest {
  id: string;
  title: string;
  description?: string;
  status?: string;
  type?: string;
  visibility?: string;
  startTime?: Date;
  endTime?: Date;
  duration?: number; // contest duration in minutes
  timeZone?: string;
  contestEndMethod?: 'manually_ended' | 'time_expired' | null;
  allowDirectEnrollment?: boolean; // Feature flag for QR/link enrollment
  participants?: string[]; // Array of user IDs enrolled in the contest
  problems: IContestProblem[];
  announcements: IContestAnnouncement[];
  createdBy?: string;
  createdAt: Date;
  updatedAt: Date;
}

interface IContestDocument extends Omit<IContest, 'id'>, Document {
  id: string;
}

const contestProblemSchema = new mongoose.Schema<IContestProblem>({
  id: { type: String, required: true },
  originalProblemId: { type: mongoose.Schema.Types.Mixed },
  title: String,
  description: String,
  difficulty: String,
  points: { type: Number, default: 0 },
  order: Number,
  timeLimit: Number,
  memoryLimit: Number,
  constraints: String,
  inputFormat: String,
  outputFormat: String,
  maxSubmissions: Number,
  partialScoring: { type: Boolean, default: false },
  customTestCases: [mongoose.Schema.Types.Mixed],
  customExamples: [mongoose.Schema.Types.Mixed],
  customStarterCode: mongoose.Schema.Types.Mixed,
  tags: [String],
  notes: String,
  examples: [mongoose.Schema.Types.Mixed],
  testCases: [mongoose.Schema.Types.Mixed],
  starterCode: mongoose.Schema.Types.Mixed,
}, { _id: false });

const contestAnnouncementSchema = new mongoose.Schema<IContestAnnouncement>({
  id: { type: String, required: true },
  message: { type: String, required: true },
  timestamp: { type: Date, default: Date.now },
  priority: { type: String, enum: ['low', 'medium', 'high'], default: 'medium' },
  isGlobal: { type: Boolean, default: true },
}, { _id: false });

const contestSchema = new mongoose.Schema<IContestDocument>({
  id: { type: String, required: true, unique: true, index: true },
  title: { type: String, required: true },
  description: String,
  status: String,
  type: String,
  visibility: String,
  startTime: Date,
  endTime: Date,
  duration: Number, // contest duration in minutes
  timeZone: { type: String, default: 'UTC' },
  contestEndMethod: { type: String, enum: ['manually_ended', 'time_expired', null], default: null },
  // Feature flag for QR/link enrollment
  allowDirectEnrollment: { type: Boolean, default: false },
  participants: [String], // Array of user IDs enrolled in the contest
  problems: { type: [contestProblemSchema], default: [] },
  announcements: { type: [contestAnnouncementSchema], default: [] },
  createdBy: String,
}, { timestamps: true });

contestSchema.index({ visibility: 1, status: 1, type: 1, createdAt: -1 });

export const Contest = mongoose.model<IContestDocument>('Contest', contestSchema);

